DROP TABLE IF EXISTS `ittc_content`;
CREATE TABLE `ittc_content` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `catid` smallint(5) unsigned NOT NULL DEFAULT '0',
  `modelid` smallint(5) NOT NULL,
  `title` varchar(120) NOT NULL DEFAULT '',
  `thumb` varchar(255) NOT NULL DEFAULT '',
  `keywords` varchar(255) NOT NULL DEFAULT '',
  `description` varchar(255) NOT NULL,
  `listorder` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `status` tinyint(2) unsigned NOT NULL DEFAULT '1',
  `hits` smallint(5) unsigned NOT NULL DEFAULT '0',
  `username` char(20) NOT NULL,
  `time` int(10) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `catid` (`catid`,`listorder`,`time`),
  KEY `time` (`catid`,`time`),
  KEY `status` (`catid`,`status`)
) ENGINE=MyISAM AUTO_INCREMENT=34 DEFAULT CHARSET=utf8;

INSERT INTO `ittc_content` VALUES('1','12','1','帕特里克·奈斯先生受聘欧盟中国联合创新中心','/data/upload/image/201703/05a1e670bd3c9182f287bd1f9914e1ee.jpg','欧洲,信息','比利时前驻华大使, 现任荣誉大使、全球协调员帕特里克•奈斯先生受聘欧盟中国联合创新中心全球高级副总裁，负责机构国际创新交流合作领域的高级管理工作。','0','1','0','admin','1488260792');
INSERT INTO `ittc_content` VALUES('2','13','1','第四届中德创新大会在柏林开幕','','创新','第四届中德创新大会13日在德国首都柏林开幕。两国政府、学术和企业等各界人士围绕创新主题，就国家创新体系、气候与能源、电动汽车、智能制造、清洁水、半导体照明、生命科学等议题展开对话。','0','1','0','admin','1460612308');
INSERT INTO `ittc_content` VALUES('3','13','1','中欧举行《中欧合作2020战略规划》首次评估会议','/data/upload/image/201604/7b5e88d746d6072c3eb187cecf3a076b.jpg','评估,战略,中欧','2016年4月6日，外交部部长助理刘海星与欧盟对外行动署亚太总司长维冈在北京共同主持了《中欧合作2020战略规划》首次评估会议，教育部、科技部、商务部及欧盟驻华代表团等相关部门参加。第十六次中国欧盟领导人会晤期间发表的《规划》为深化中欧合作描绘了蓝图，为构建中欧和平、增长、改革、文明四大伙伴关系提供了重要基础和支撑。','0','1','0','admin','1460007555');
INSERT INTO `ittc_content` VALUES('4','13','1','外媒：科技创新助推中国经济转型','','中国经济,创新','新华社北京４月８日电（记者于嘉）境外媒体近期持续关注以互联网为代表的中国科技产业的发展。外媒认为，尽管中国经济增速放缓，但以“互联网＋”“工业４．０”为代表的数字经济发展迅速，不仅将提高生产效率，还能引领消费新趋势，为经济发展提供新动能。','0','1','0','admin','1460094039');
INSERT INTO `ittc_content` VALUES('5','13','1','欧洲议会支持欧盟科研信息数据开放共享','','欧洲,信息','欧洲议会科研与创新委员会，近日全票通过欧委会制定的欧盟科研信息开放共享政策指导性文件。欧洲议会要求欧盟成员国积极采取具体政策行动加以落实，要求欧盟层面公共财政基金资助支持的科研与创新项目全面开放相关信息数据，支持科技界充分利用“文本与数据挖掘”技术（TDM）开展研发创新活动，支持数字时代的研发创新活动“版权例外”。','0','1','0','admin','1454391692');
INSERT INTO `ittc_content` VALUES('6','13','1','欧盟未来两年将投巨资促科研与创新','','创新','根据 “2016－17工作方案”，欧盟委员会将在未来两年内投资约160亿欧元推动科研与创新，以增强欧盟的竞争力。这属于欧盟“地平线2020”科研规划的一部分。','0','1','0','admin','1444801324');
