  PRIMARY KEY (`id`),
  KEY `catid` (`catid`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

INSERT INTO `ittc_content_news` VALUES('1','12','&lt;p align=&quot;center&quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;/data/upload/image/201703/05a1e670bd3c9182f287bd1f9914e1ee.jpg&quot; /&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	比利时前驻华大使, 现任荣誉大使、全球协调员帕特里克•奈斯先生受聘欧盟中国联合创新中心全球高级副总裁，负责机构国际创新交流合作领域的高级管理工作。奈斯先生表示将为机构的发展竭尽全力。奈斯先生的加入，提升了欧盟中国联合创新中心的国际视野，他丰富的经验将为机构的发展做出贡献。\r\n&lt;/p&gt;');
INSERT INTO `ittc_content_news` VALUES('2','13','&lt;p&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 第四届中德创新大会13日在德国首都柏林开幕。两国政府、学术和企业等各界人士围绕创新主题，就国家创新体系、气候与能源、电动汽车、智能制造、清洁水、半导体照明、生命科学等议题展开对话。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 中国科技部部长万钢在开幕式上介绍说，科技创新在中国经济发展中发挥着举足轻重的作用，已成为转变发展方式的动力。中国政府将秉承创新、协调、绿色、开放、共享的发展理念，将创新摆在国家发展全局的核心位置，深入实施创新驱动发展战略，深化科技体制改革，发挥科技创新在全面创新中的引领作用，全面推进大众创业、万众创新。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 万钢表示，近年来，中德两国政府高层互动频繁，两国关系得到进一步深化，这也为中德科技创新合作开拓了更广阔的空间，“创新是推动中德两国合作的强大动力，是两国社会经济可持续发展的共同需求”。万钢说，“本次会议就是要明确下一步创新的方向，确定合作的重点，构建合作的平台来聚集创新的人群”。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 德国联邦教研部国务秘书格奥尔格·许特说，中德两国都是创新大国，互相钦佩对方在高科技领域的能力和实力，这为推进中德创新合作提供了良好基础。两国可在科研政策、基础研究以及工业4.0、环境保护等技术领域加强合作，共同应对全球性挑战。年轻人是新想法的源泉，两国将为青少年交流创造更多便利条件。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 本次会议为期两天。200余名中德专家学者将就国家创新体系、城镇化领域创新、两国科技合作现状与未来3大议题展开深入交流研讨。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 不少与会者表示，中德两国创新合作意义重大，双方有必要进一步深化这种互利双赢的合作，共同为全球性挑战找到解决方案。未来5至10年内，中德两国有望在工业4.0对接中国制造2025、智能生态城市等领域开展更为紧密的合作。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 中德创新大会由中国科技部与德国教研部共同举办，是中德创新平台框架下的重要对话。2011年，中国科技部与德国教研部共同建立中德创新平台，作为两国在创新领域交流对话的长效机制。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;');
INSERT INTO `ittc_content_news` VALUES('3','13','&lt;p align=&quot;center&quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;/data/upload/image/201604/7b5e88d746d6072c3eb187cecf3a076b.jpg&quot; /&gt; \r\n&lt;/p&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 2016年4月6日，外交部部长助理刘海星与欧盟对外行动署亚太总司长维冈在北京共同主持了《中欧合作2020战略规划》首次评估会议，教育部、科技部、商务部及欧盟驻华代表团等相关部门参加。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 第十六次中国欧盟领导人会晤期间发表的《规划》为深化中欧合作描绘了蓝图，为构建中欧和平、增长、改革、文明四大伙伴关系提供了重要基础和支撑。 &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp; 中欧双方充分肯定《规划》启动两年多来的执行情况，认为《规划》落实有力、成果显著，对促进中欧发展战略对接和全方位合作发挥了重要作用。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 双方决定继续加强政治对话，深挖合作潜力，进一步拓展经贸投资、互联互通、数字经济、科技创新、绿色发展、人文交流、G20及全球治理等重点领域合作，争取中欧发展战略对接尽快取得早期收获，促进中国与欧盟及成员国关系更快更好地协调发展，并为中欧双方人民带来更多福祉。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 双方同意将此次评估会的情况向双方领导人汇报，以利做好第十八次中国欧盟领导人会晤筹备工作。\r\n&lt;/p&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p align=&quot;left&quot;&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;');
INSERT INTO `ittc_content_news` VALUES('4','13','&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; 新华社 北京４月８日电 境外媒体近期持续关注以互联网为代表的中国科技产业的发展。外媒认为，尽管中国经济增速放缓，但以“互联网＋”“工业４．０”为代表的数字经济发展迅速，不仅将提高生产效率，还能引领消费新趋势，为经济发展提供新动能。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; 法国《费加罗报》文章称，“互联网＋”成为中国实现经济升级、转向服务驱动战略的先锋，“中国正成为唯一有能力在技术上挑战美国的重要力量”。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; 美国《华尔街日报》报道称，中国的互联网公司正将触角伸向零售、金融和制造等行业。科技公司在中国百强品牌中所占的分量越来越大，市值占比从两年前的１６％上升到去年的２７％，许多公司的海外业务也越做越大。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; 美国消费者新闻与商业频道网站称，在移动互联网等领域的创新技术助力下，中国的移动购物产业已领先美国。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; 一些外媒注意到科技产业引领下中国消费市场的新现象。澳大利亚商业旁观者网站说，中国消费者正以闪电般的速度适应电子化世界。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; 美国《福布斯》双周刊网站报道指出，中国正在出现一批“全渠道消费者”。他们推动电子商务发展并将驱动今后１０年的中国经济增长。数字平台是中国消费者了解各大品牌以及购物体验的重要途径，而“全渠道消费者”正通过社交媒体、电子商务网站及其他应用软件和媒介渠道来购物。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; 部分外媒聚焦中国工业领域的科技创新与升级。法国《回声报》题为《当中国成为数字大国》的文章说，中国正在数字领域占据强势地位并全力推行经济的数字化，由“互联网＋”支持的中国“工业４．０”已经走在路上。文章说，中国在领导全球经济数字化方面拥有与美国一较高低的重要王牌，并已进入美国、德国、日本、韩国所在的工业自动化程度最高的俱乐部。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; 英国《金融时报》报道说，全球正要跨入第四次工业革命“数字经济”的门槛，中国为此投身于技术革新，提出“中国制造２０２５”的关键词，将工业的数字化作为推动产业升级的主要动力。&lt;br /&gt;\r\n汇丰银行大中华区行政总裁黄碧娟在澳大利亚商业旁观者网站撰文说，网上购物正改变中国经济面貌并改变了整个供应链，特别是将定价权放在广大消费者手中。微信、淘宝等平台为供应商提供了公平的竞争环境。中国决策者正通过“互联网＋”政策引领经济改革，培育消费拉动的经济。文章说，对互联网的高度接受为中国经济注入了更多市场力量、透明度和竞争因素，从而确保质量、价格、效率和服务等比以往得到更好的回报。文章还说，“互联网＋”相关政策的实施将在未来数年为中国经济提供动能。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; 美国《福布斯》双周刊网站文章认为，互联网、智能设备和社交媒体已成为中国消费者日常生活的核心部分，这为创新者和企业家都提供了巨大的机会。&lt;br /&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;');
INSERT INTO `ittc_content_news` VALUES('5','13','&lt;p&gt;\r\n	&lt;span&gt;&lt;span style=&quot;color:#333333;font-family:宋体;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; 欧洲议会科研与创新委员会，近日全票通过欧委会制定的欧盟科研信息开放共享政策指导性文件。欧洲议会要求欧盟成员国积极采取具体政策行动加以落实，要求欧盟层面公共财政基金资助支持的科研与创新项目全面开放相关信息数据，支持科技界充分利用“文本与数据挖掘”技术（TDM）开展研发创新活动，支持数字时代的研发创新活动“版权例外”。欧盟层面公共财政资助的研发创新活动基金主要包括：欧盟2020地平线（H2020）、欧盟结构与投资基金（ESI）、欧盟融合基金（ECF）、欧盟竞争力与创新基金（ECIF）、欧盟共同农业政策（CAP）和欧盟互联互通基金（CEF），以及欧盟层面各类风险投资便利机制，如欧盟科技卓越和创新型中小企业投融资便利机制等。欧盟2020地平线，将在其中扮演关键的协调角色，包括协调成员国公共科研基金资助支持的研发创新活动。&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; 欧委会新一届科研与创新事务委员莫达斯（MOEDAS）新推出的欧盟“开放科学开放创新”政策，同时获得欧洲议会的支持。根据欧盟里斯本条约和欧盟2020战略，建立欧洲数字单一市场和欧盟共同研发创新政策框架，被确定为欧盟重大优先政策目标。获取欧洲议会的支持，将为实现目标奠定政治基础。 &lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp; 莫达斯在文件获得通过后的新闻发布会上指出，欧盟28个成员国数字信息与科技创新市场的碎片化，是制约欧盟科技创新卓越、工业企业竞争力和应对经济社会挑战的最主要障碍，必须彻底清除，从而确保欧盟数字经济社会和科技创新市场的世界领先水平，确保先发优势。&amp;nbsp;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span&gt;&lt;span style=&quot;color:#333333;font-family:宋体;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;&quot; class=&quot;MsoNormal&quot; align=&quot;left&quot;&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;');
INSERT INTO `ittc_content_news` VALUES('6','13','&lt;p&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 新华网 根据 “2016－17工作方案”，欧盟委员会将在未来两年内投资约160亿欧元推动科研与创新，以增强欧盟的竞争力。这属于欧盟“地平线2020”科研规划的一部分。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 欧盟委员会在公报中介绍，新的工作方案旨在为跨领域科研与创新计划提供支持。其中，为欧洲制造业的现代化投资10亿欧元；为自动驾驶技术的研发和标准投资1亿欧元；为发展物联网投资1．39亿欧元；为建立更加强劲和可持续发展的经济体系投资6．7亿欧元；为智能和可持续城市领域投资2．32亿欧元。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 此外，欧盟还计划投资至少800万欧元，研究欧盟的外部边境安全，帮助识别并避免人口贩卖和走私行为；投资2700万欧元研发预防犯罪和恐怖主义的新技术；投资1500万欧元研究欧洲人口迁移的源头和影响；投资500万欧元应对给橄榄树带来毁灭性影响的叶缘焦枯病菌。&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 欧盟委员会强调，“2016－17工作方案”将通过项目征集、公共采购以及“地平线奖”等方式提供资助。负责科研和创新事务的欧盟委员卡洛斯•莫埃达斯说：“科研与创新是欧洲进步的‘引擎’，对解决当下出现的难民、气候变化、清洁能源等新挑战来说至关重要。”&lt;br /&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 2013年底，投资总额达770亿欧元的欧盟“地平线2020”科研规划正式启动，这是欧盟的第八个欧盟科研框架计划。其中，2014年和2015年两年的预算为150亿欧元。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;');
INSERT INTO `ittc_content_news` VALUES('7','36','&lt;p&gt;\r\n	BEIJING - China has vowed to improve standards and quality in equipment manufacturing.&lt;br /&gt;\r\nThe promise is the latest move in the ambitious &quot;Made in China 2025&quot; program, which aims to develop the country into a powerhouse of high-end manufacturing.&lt;br /&gt;\r\nChina will aim to bring over 90 percent of domestic standards in key manufacturing areas up to international levels by 2020, up from the current 70 percent, said a statement issued Wednesday after a State Council conference chaired by Premier Li Keqiang.&lt;br /&gt;\r\nResearch and development of major industry norms in robotics, rolling stock, agricultural machinery and sophisticated medical devices should be accelerated, said the statement.&lt;br /&gt;\r\n&quot;We will formulate high standards to spur the upgrade of \'made in China\' goods,&quot; Li said at the meeting.&lt;br /&gt;\r\nFor years, China has served as the &quot;world factory,&quot; churning out merchandise ranging from smartphones to diapers for consumers around the globe. But the country\'s manufacturers still have not mastered core high-end production technology and face a huge gap with industry leaders from Germany and the United States.&lt;br /&gt;\r\nAs a consequence, shoppers fly abroad to purchase consumer goods such as heated toilet seats, and cross-border e-commerce has become wildly popular. The manufacturing of automobiles, numerical control machines and other major industrial products still depends on foreign technology.&lt;br /&gt;\r\nA national plan, &quot;Made in China 2025,&quot; was unveiled in May 2015 to address the problem and build the country into a manufacturing power within ten years.&lt;br /&gt;\r\n&quot;We should work to improve consumer confidence in domestic production, enhance manufacturing quality and efficiency and increase the global competitiveness of Chinese companies,&quot; Li said.&lt;br /&gt;\r\nNing Jiajun, member of the Experts Committee of the State Information Center, described formulating high-level manufacturing standards as a precondition for China to realize the goal. &quot;The failure of industry and technological norms to develop with the times is common in quite a few sectors in China. This has become a major bottleneck for industrial upgrades.&quot;&lt;br /&gt;\r\nThe process of lifting the standards, which can be tough and costly, is indispensable for the country\'s shift from rough manufacturing to more value-added production, Ning said.&lt;br /&gt;\r\nAnalysts expect the new resolution to propel the standardization of core technologies in multiple fields and help lay the foundation for supply-side reform.&lt;br /&gt;\r\nThe government should toughen supervision to create a fair environment and advocate &quot;the spirit of the craftsman&quot; among manufacturers, Li said.\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;');
INSERT INTO `ittc_content_news` VALUES('14','36','&lt;p&gt;\r\n	The pace of China\'s investment activities in Europe seems to be quickening, even as European investment remains stuck at historically below-average rates.&lt;br /&gt;\r\nToday, this mutually-advantageous fit is largely praised in the two regions. In future, however, certain points of contention may emerge, driven mainly by the concerns of European and other host countries. These issues will need to be addressed early on in order for the relationship to continue to thrive.&lt;br /&gt;\r\nNowhere is the positive effect of Chinese investment felt more clearly than in Greece. As Greece goes through its ninth year of recession, having seen its capital stock steadily shrink throughout this period and incoming foreign investment sharply decline, one of the few bright spots on the business horizon has been the expanding presence of Cosco at the port of Piraeus.&lt;br /&gt;\r\nOne of the first major Chinese direct investments in the EU, Cosco\'s management of the two terminals at Greece\'s largest port since 2008 has seen commercial traffic going through the port increase eightfold. In January, the company was awarded a majority stake in the remaining State-run facilities at the port.&lt;br /&gt;\r\nBy going against the current, this move solidified the perception, in Greece, of China as a steadfast partner that not only talks the talk, but walks the walk.&lt;br /&gt;\r\nIn some ways, Greece may be a special case. It is not threatened by adverse technology flows. In the case of Greece, Cosco\'s initiatives are aimed at turning the country into a transshipment point and distribution center for Europe.&lt;br /&gt;\r\nTo this end, it has negotiated agreements with major multinationals, such as Hewlett-Packard and the Chinese telecom giant ZTE－not the most strategically-sensitive kind of deal.&lt;br /&gt;\r\nDeclarations about the benefits to the economies of Europe and other regions from Chinese direct investment are numerous, if not always free of caveats and warnings: This is a unique opportunity to attract capital to Europe and help restart investment and economic growth; innovation spillovers may occur; complements may be available and links strengthened between the host countries and one of the most important, expanding regions of the world.&lt;br /&gt;\r\nHowever, new types of challenges may soon surface: China is clearly different from other investor countries. Specific concerns relate to the nature of China\'s economic system, for example subsidies and the lack of openness to FDI in China. Foreign ownership of assets can create problems if it provides a foreign power with the means to control, monitor and appropriate critical strategic goods, including certain sensitive technologies and infrastructure. Because of China\'s uniqueness, policymakers will need to be equipped with real and effective policy tools to defend the principle of investment openness against potential populist and local revolts.&lt;br /&gt;\r\nRecent initiatives by Washington, Brussels and Beijing, such as the agreement last January on the scope of an ambitious EU-China investment agreement covering such important issues as market access, nondiscrimination, key regulatory matters, protection for investors, and environmental and labor issues, are, however, indicative of a deeper understanding on the part of all partners of the importance of the issues at stake.&lt;br /&gt;\r\nThese three poles－the US, the EU and China－together account for more than 67 percent of the world\'s outbound FDI. Building trust via such improvements in global governance structures can go a long way toward making the smooth insertion of China into the global economy a reality to be feted by all.\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&amp;nbsp;\r\n&lt;/p&gt;');
INSERT INTO `ittc_content_news` VALUES('12','36','&lt;p&gt;\r\n	The last ten years have seen significant developments in the legal framework related to intellectual property in China, according to the European Union Chamber of Commerce in China. &lt;br /&gt;\r\nAs its market opens up, China\'s IP system will need to continue to evolve if it is to support the needs of Chinese and international businesses operating in China and maintain a fair and competitive market environment, it said.&lt;br /&gt;\r\nAccording to Adam Dunnett, Secretary General of European chamber, the EU-China IP Dialogue Mechanism has proved instrumental in helping to lay sound foundations for China\'s IP system and ease its convergence with global IP systems.&lt;br /&gt;\r\nInnovation is the answer why fair implementation of IPR legislation so important and worth emphasizing, said the chamber.&lt;br /&gt;\r\nInnovation can only flourish within a clear legal framework that protects the IP of those companies who have invested in, and are committed to, developing innovative products and services, it said.&lt;br /&gt;\r\nWith policy initiatives such as China Manufacturing 2025, the Chinese government has made clear their commitment to creating the right conditions to foster innovation and the important role this will play in China\'s economic growth over the coming years.&lt;br /&gt;\r\nTo mark the 10th anniversary of the EU-China IP Dialogue Mechanism, an intention to create a platform where issues related to the protection and enforcement of IP could be discussed set up10 years ago, the European Chamber held a conference on IPR, Innovation and Development in Beijing on Wednesday.&lt;br /&gt;\r\nThe conference touched on how companies, both foreign and Chinese, innovate in practice, and explored what they are doing to protect their IPR, according to the chamber.&lt;br /&gt;\r\nIt will also examine the various IPR protection mechanisms that are already in place and the challenges and opportunities that have arisen as a result of recent reforms to IPR legislation.&lt;br /&gt;\r\nAccording to the chamber, the EU-China IP Dialogue Mechanism has been complemented down the years by supporting projects such as the EU-China Trade Project and the EU-China Project on the Protection of Intellectual Property Rights.&lt;br /&gt;\r\nSome of the more significant contributions stemming from these projects have come from legislative exchanges, peer exchanges and comparative analysis, which have helped to increase knowledge and develop common understandings, it said.&lt;br /&gt;\r\nThese interventions have substantially contributed to the adjustment of China\'s national IP legislation, which has come a long way to strengthening economic relations between the EU and China. &lt;br /&gt;\r\nThe drafting process of China\'s laws is a good example of how this has achieved a positive result, and the process has allowed for extensive comments and suggestions to be submitted by European businesses, it said.&lt;br /&gt;\r\nWith more emphasis on the protection of intellectual property rights, the Chinese government has been coming up with greater improvements in legal protections and enforcement.&lt;br /&gt;\r\nIn 2011, the State Council, or the country\'s cabinet, released a guideline for improving efforts in combating IPR infringements and the manufacturing and sale of fake and shoddy products.&lt;br /&gt;\r\nChina also places great emphasis on the protection of business secrets, and encourages companies to step up technological research and development to enhance their competitiveness, the ministry said.\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;');
INSERT INTO `ittc_content_news` VALUES('15','36','&lt;p&gt;\r\n	BRUSSELS, March 28 (Xinhua) -- China and the Central and Eastern European countries (CEEC) have witnessed fruitful and flourish cooperation on areas including interconnection, finance, agriculture and culture since the establishment of China-CEEC mechanism, also known as the 16+1 mechanism.&lt;br /&gt;\r\nChina’s “One Belt One Road (OBOR)” initiative was linked to European investment plan via CEEC, which also bond the exchanges and cooperation between Asia and Europe. On the other hand, the initiative also helps promote the two regions’ cooperation.&lt;br /&gt;\r\nCHINA-ROMANIA: ENERGY COOPERATION FLOURISH&lt;br /&gt;\r\nChina and Romania have witnessed solid development of their economic cooperation since the“16 plus 1 cooperation” mechanism was established. Meanwhile, the initiative helps inject vigor into their coordination particularly in energy section.&lt;br /&gt;\r\nThe two sides have struck an agreement to jointly construct the units 3 and 4 of the Cernavoda nuclear power plant last year. Once the units are built, the plant will manage to cover nearly 40 percent of Romania’s national electric generating capacity.&lt;br /&gt;\r\nThe agreement, worth up to seven billion euros, stands not only as the most significant project between China and Romania but also the landmark program between China and CEEC.&lt;br /&gt;\r\nThe construction and operation of the two new units will greatly improve Romanian energy supply situation, help the country to become an important energy producer in the region and making contribution to the energy independence of Romania or even theEuropean Union, according to Romanian energy ministry.&lt;br /&gt;\r\nAt the same time, the project will provide huge orders for the country’s industry and boost its economic growth, the ministry said.&lt;br /&gt;\r\nChina and Romania are speeding up their cooperation, conducting negotiations on projects including nuclear, heat-engine, hydroelectricity, highways, which worth to several billion euros, according to the Romanian side.&lt;br /&gt;\r\nRomania will work to implement series of cooperation projects with China on energy, infrastructure and digital technology, said Costin Borc, Romanian vice prime minister and minister of economy, commerce and relations with the business environment.&lt;br /&gt;\r\nCHINA-POLAND: HISTORIC AND CURRENT COOPERATION BOOSTED&lt;br /&gt;\r\nPoland is a vital stop on the roadmap of the OBOR initiative as the country sits at the crossroad of Europe and enjoyed the sound advantage in geography.&lt;br /&gt;\r\nChina and Poland, together in 1951, invested the Chinese-Polish Joint Stock Shipping Company, the first sino-foreign joint venture since the year of 1949 when China was established. The company has gained rapid development and made record profits over the past 15 years. Currently, its operating area covers east and south Asia, Mideast, which are within the targeted areas of the OBOR initiative.&lt;br /&gt;\r\nGovernments and enterprises from both sides have exchanged frequently since the implementation of the initiative, which is reportedly viewed by Poland as a chance to develop the country into a transfer center for the logistic and goods in the region.&lt;br /&gt;\r\nNew opportunities for the land trade ties between China and Poland were brought by China-Europe trains including the Chengdu-Europe Express Railway and &quot;Sumanou”, two non-stop trains which run from China to Poland help inject momentum into China-Poland economic ties.&lt;br /&gt;\r\nPoland, particularly its central city Lodz, will benefit from the OBOR initiative, Polish experts said. Lodz, which sits in the heart of Europe and owns completed transportation, will stand as a promising logistic hub for Poland or even for the Central and Eastern Europe, they argued.&lt;br /&gt;\r\nLodz attaches great importance to the cooperation with Chinese local governments and companies, said Deputy Mayor of Lodz Marek Cieślak. He expressed the hope that Poland and China will jointly build the Chengdu-Europe Express Railway, of which the destination is Lodz, into a crucial stop on Chinese Silk Road Economic Belt.&lt;br /&gt;\r\nSERBIA: CHINA IS A VITAL PARTNER&lt;br /&gt;\r\nThe cooperation between China and CEEC is on the basis of OBOR and Serbia is an important partner for China to make the initiative into practice, said the Serbian side.&lt;br /&gt;\r\nThere are lots of landmark projects between two countries including the construction of the &quot;Mihailo Pupin&quot; bridge across the Danube in Belgrade; repair and construction of new block and the expansion of (coal) mines of the thermal power plant &quot;Kostolac&quot;; the construction of highway sections of the Corridor 11.”&lt;br /&gt;\r\n“Serbia has in recent years become a kind of Chinese infrastructural polygon in Europe,” said Branko Zujovic, a senior Serbian journalist and writer.&lt;br /&gt;\r\nChina arrived to the Central and Eastern Europe at a time when the mentioned regions felt significant consequences of the great crisis that began in 2008. It brought to the region its capital, opportunity for development and commitment to invest in mutually beneficial projects. This is a great impulse for the economies of these countries, but also for the stability of Europe, he said.&lt;br /&gt;\r\nThe fundament of the OBOR is not just the exchange of goods, economy, money, but the exchange of cultures, languages, values that motivate mutual understanding. The initiative is extremely important and its scale is yet to be seen in next several decades, said Zarko Obradovic, first Serbian coordinator of the summit of China and 16 countries in the region.&lt;br /&gt;\r\nCEEC PIN HOPE ON “16 + 1 COOPERATION”&lt;br /&gt;\r\nThe “16+1 Cooperation”mechanism has generated fruitful outcome since its establishment and continues to be appealing to CEEC.&lt;br /&gt;\r\nThe Association for the Promotion of Agricultural Cooperation between China and the CEE Countries (APACCCEEC), established in last June in Bulgaria, operated a website and so far has registered 70 more companies from China and CEEC which seek business opportunities.&lt;br /&gt;\r\n&quot;In the past two years we see that there isn’t a single country among them that does not wish to be a part of the project, and doesn’t wish for something from the project to be realized,&quot; said Obradovic.\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&amp;nbsp;\r\n&lt;/p&gt;');
INSERT INTO `ittc_content_news` VALUES('13','35','&lt;p align=&quot;center&quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;/data/upload/image/201703/ea1b2886ee9db8d23782ec0ed300903b.jpg&quot; /&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	Mr. Patrick Nijs, Honorary Ambassador, Global Facilitator, and Former Ambassador of Belgium to China, accepted the appointment as the Global Senior Deputy President of EUCJIC. Mr. Nijs will be in charge of the senior management of EUCJIC’s international technology transfer, transformation, exchange and communication. He will spare no effort for the development of EUCJIC. Mr Nijs’s joining enriches EUCJIC’s international perspectives and his rich experience will make great contributions to EUCITTC.&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;');
INSERT INTO `ittc_content_news` VALUES('16','36','&lt;p&gt;\r\n	BRUSSELS, April 7 (Xinhua) -- Europe is expected to benefit from the implementation of China\'s new five-year development plan, said Head of Chinese Mission to the European Union (EU) Yang Yanyi on Thursday.&lt;br /&gt;\r\nSuccessfully implemented, the 13th Five-Year Plan, the country\'s economic and social development blueprint for the 2016-2020 period, will facilitate the EU\'s effort to set its recovery on a sustainable path, Yang said at the European Parliament.&lt;br /&gt;\r\nThe 13th Five-Year Plan, to put China on a qualitative and more sustainable growth trajectory and further galvanize global growth, will be favorable for the 28-country bloc to unlock investment, foster productivity and accelerate the process of convergence, Yang said.&lt;br /&gt;\r\nChina set its gross domestic product (GDP) growth target in a range of 6.5 percent to 7 percent this year in the plan, trying to restructure its economy into a more sustainable one driven by domestic consumption and service sectors.&lt;br /&gt;\r\nThe shift will lead to rising demand by China, which is a huge market with the population of over 1.3 billion people, for imports of consumer goods and services, and help boost the EU\'s exports to China, the ambassador said.&lt;br /&gt;\r\nThe world\'s second largest economy, according to the Plan, will speed up the implementation of its free trade strategy, which is supposed to boost negotiations on investment agreement between China and the EU as well as moving towards broader ambitions including China-EU free trade agreement.&lt;br /&gt;\r\nMeanwhile, European companies will enjoy more opportunities to use their technological forte to gain foothold in the Chinese market including in such areas as innovation, connectivity, information technologies, urbanization, finance, clean energy, services and circular economy, the ambassador said.&lt;br /&gt;\r\nChina and the EU are both major partners to tackle climate change. In the 13th Five-Year Plan, China outlined ambitious target to cut its carbon dioxide emissions by 18 percent by 2020.&lt;br /&gt;\r\nChina\'s moving toward a cleaner, greener model of economy will have big implications for the environment and China-EU cooperation on climate change, Yang said.&lt;br /&gt;\r\nChina has set 2020 as the target year to realize the first &quot;centenary goal&quot; of building a moderately prosperous society in all respects, pledging efforts to double GDP and per capita personal income from the 2010 level.&lt;br /&gt;\r\nThe five-year plan aims to keep medium-high growth in the next five years. By 2020, the size of China\'s economy is expected to exceed 90 trillion yuan (13.8 trillion U.S. dollars), compared with 67.7 trillion yuan in 2015.&lt;br /&gt;\r\nIn March, Chinese lawmakers voted in favor of the five-year plan during the annual session of the National People\'s Congress.\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;');
INSERT INTO `ittc_content_news` VALUES('21','36','&lt;p&gt;\r\n	LONDON, March 13 (Xinhua) -- Chinese Ambassador to Britain Liu Xiaoming on Monday spoke highly of the British role in helping build the Belt and Road (B&amp;amp;R) to revive the ancient Silk Road.&lt;br /&gt;\r\n&lt;span style=&quot;line-height:1.5;&quot;&gt;In a signed article entitled &quot;Britain is helping us build a new Silk Road&quot; on the Daily Telegraph, Liu said Britain is a country of global influence and can be an important partner for China in B&amp;amp;R.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;He said that Britain has many strengths and unique advantages that could give it a head start in B&amp;amp;R cooperation, referring to the country\'s highly internationalised financial sector, mature professional services in law and consulting, prestigious think tanks and educational institutions as well as world-class R&amp;amp;D and innovation platforms, among others.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;He stressed that these strengths and advantages put Britain in an excellent position to secure the opportunities the B&amp;amp;R has to offer.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;&quot;China and the UK can continue to advance their respective development strategies in tandem, can expand trade and investment, jointly explore and develop the market along the B&amp;amp;R route, and deliver greater common prosperity,&quot; Liu said.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;He said that the remarkable opportunities of &quot;Belt and Road&quot; cooperation are now up for grabs.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;&quot;With China, Britain can be a key partner, reaping the potential of these opportunities,&quot; Liu said. &quot;By ensuring we pull together, the B&amp;amp;R initiative can, like the Silk Road before it, go a long way to delivering better lives for many millions of people, from Asia to Europe.&quot;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;');
INSERT INTO `ittc_content_news` VALUES('22','36','&lt;p style=&quot;text-align:left;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201703/3ef7a3df8cf79739b2c8afc9865cd06f.jpg&quot; alt=&quot;&quot; /&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;BEIJING, Feb. 21 (Xinhua) -- China and France on Tuesday signed agreements on nuclear energy and science during French Prime Minister Bernard Cazeneuve\'s Beijing visit.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;During talks with Cazeneuve, Chinese Premier Li Keqiang said China is willing to work with France to deepen cooperation in the whole industrial chain of nuclear energy.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;He hoped that the two sides would ensure a smooth implementation of the Hinkley Point C nuclear power plant program and discuss joint development of the third-party nuclear power market.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;Li suggested the two sides explore cooperation in civil aviation and space industries, deepen cooperation in coping with climate change, and speed up the eco-city project in Wuhan, capital city of central China\'s Hubei Province.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;He also hoped the two countries to explore cooperation in whole industrial chain of agriculture, medicine and health, as well as coping with an aging society.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;Li urged the two sides to facilitate bilateral people-to-people exchanges, effectively implement the Sino-French &quot;1000 Interns&quot; program and the mutual recognition agreement of driving license just signed between the two sides.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;He called on the two nations to push the social security agreement signed last October into effect as soon as possible, which exempts company employees assigned to work in each other\'s countries from the mandatory social security contributions.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;The premier hoped that the French side would provide Chinese tourists with better services.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;On bilateral relations, Li said China and France both serve as the permanent members of the UN Security Council, and the Sino-French ties have been at the forefront of China\'s links with western countries.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;He said the two countries should always uphold the principle of mutual respect, equality, cooperation, mutual benefit and win-win and strengthen communication and coordination in international affairs.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;He called on the two sides, against the backdrop of the complicated and volatile international situation, jointly send out strong signals of safeguarding world peace and stability and promoting development, so as to respond to uncertainty with stability.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;China firmly supports European integration, and hopes to see a united, prosperous and stable European Union(EU), which is not only conducive to China-France and China-Europe relations, but also in the interests of multilateralism and the development of globalization, said Li.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;Li urged the EU to comprehensively and thoroughly fulfill its obligations under the Protocol on China\'s Accession to the World Trade Organization (WTO), so as to create conditions for sound and healthy development of China-Europe pragmatic cooperation.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;Under Article 15 of the Protocol on China\'s Accession to the WTO, the EU should have ended its &quot;surrogate country system&quot; by Dec. 11, 2016.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;For his part, Cazeneuve praised the fruitful cooperation with China in civilian nuclear energy sector, adding bilateral cooperation in agriculture and environmental protection is exemplary.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;France is ready to work with China to enhance cooperation in such areas of politics, economic and trade, investment, science, agriculture, nuclear energy, environmental protection and education, he said.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;France is willing to make efforts with China to maintain trade liberalization and defuse protectionism, he added.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;Noting Europe and China share broad common interests, Cazeneuve said the two sides will find proper solution to the implementation of the Article 15 of the Protocol on China\'s Accession to the WTO and France is willing to make efforts in this regard.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;When jointly meeting the press with Cazeneuve, Li reaffirmed that China upholds to the principle of openness, inclusiveness, and win-win cooperation, opposes to all kinds of protectionism, and safeguards an open international trade system as well as the multilateral framework.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;Cazeneuve said France is ready to work with China to oppose trade protectionism and promote the development and prosperity of the two countries.&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;Cazeneuve is paying an official visit to China on Feb. 21-23. Besides Beijing, Cazeneuve will also visit Wuhan.&lt;/span&gt;\r\n&lt;/p&gt;');
INSERT INTO `ittc_content_news` VALUES('25','12','&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;2017年3月16日 欧盟中国联合创新中心一行对斯里兰卡进行为期一周的考察访问。期间分别与斯里兰卡总理维克勒马新哈、斯里兰卡中央省省长Mr.Sarath Ekanyake、斯里兰卡议长卡鲁·贾亚苏里亚进行会晤洽谈，在多个领域寻求合作与发展，就展开有效合作的具体方式达成一致共识。&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/192ddc351cc2a28b87dedafea80ccdd0.jpg&quot; alt=&quot;&quot; /&gt; \r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;span style=&quot;line-height:1.5;&quot;&gt;与斯里兰卡总理 维克勒马新哈会谈&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;br /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/c38d3abcccfd057eb3a3cbd232dffd25.jpg&quot; alt=&quot;&quot; /&gt; \r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;span style=&quot;line-height:1.5;&quot;&gt;与中央省省长 Mr.Sarath Ekanyake&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;br /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/655383ba02f4d8c5e6523ef841da4b00.jpg&quot; alt=&quot;&quot; /&gt; \r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;span style=&quot;line-height:1.5;&quot;&gt;与斯里兰卡议长 卡鲁·贾亚苏里亚&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;br /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/5045358e7765e47cae518eb7fbe2e080.jpg&quot; alt=&quot;&quot; /&gt; \r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;span style=&quot;line-height:1.5;&quot;&gt;与斯里兰卡企业家交流合作&lt;/span&gt; \r\n&lt;/div&gt;');
INSERT INTO `ittc_content_news` VALUES('24','35','&amp;nbsp; &amp;nbsp; &amp;nbsp;On March 16th, 2017, the delegation headed by EUCJIC was visiting Sri Lanka for a week upon invitation. The delegation met with Mr. Ranil Wickremesinghe, the Prime Minister of Sri Lanka, Mr.Sarath Ekanyake, the Chief Minister of the Central Province of Sri Lanka and Hon. Karu Jayasuriya, the Speaker of the Sri Lanka Parliament, where warmly discussions of the cooperation opportunities in various fields had been made and both parties have reached a consensus on the specific mode of effective cooperation.&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/1c9096e5e3871128526a2cb1417023bd.jpg&quot; alt=&quot;&quot; /&gt; \r\n&lt;/div&gt;\r\n&lt;p style=&quot;text-align:center;&quot;&gt;\r\n	&lt;span style=&quot;line-height:1.5;&quot;&gt;Meeting with Mr. Ranil Wickremesinghe, Prime Minister of Sri Lanka&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/ce8b313d728a35e697c6c68857f3bf7b.jpg&quot; alt=&quot;&quot; /&gt; \r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;span style=&quot;line-height:1.5;&quot;&gt;Meeting with Mr.Sarath Ekanyake, Chief Minister of the Central Province of Sri Lanka&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/4d2fe535fd6003d58e1967e397626802.jpg&quot; alt=&quot;&quot; /&gt; \r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;span style=&quot;line-height:1.5;&quot;&gt;Meeting with Hon. Karu Jayasuriya, Speaker of the Sri Lanka Parliament&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/3ab9f9b33660fa1465a88e9175216b97.jpg&quot; alt=&quot;&quot; /&gt; \r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;span style=&quot;line-height:1.5;&quot;&gt;Meeting with Sri Lankan Entrepreneurs&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/div&gt;');
INSERT INTO `ittc_content_news` VALUES('26','35','&amp;nbsp; &amp;nbsp; &amp;nbsp;On March 30th and 31st, 2017, together with Hungarian technologically advanced enterprises and research institutions in soil remediation, biomass, bio-remediation, energy plant and others, the delegation headed by Mr. Peter Huang from EUCJIC attended Hungarian-Chinese Agro-Technology Workshop held by the Embassy of Hungary in Beijing. After the discussion with Dr. Gyozo Jordan from Institute for Environmental Science of Szent Istvan University, Mr. Sándor Pákozdi from Arundo Celluloz Farming Ltd and Ms. Ágnes Kárteszi, first secretary for agriculture and environment at Embassy of Hungary in Beijing, consensus has been reached on Sino-Hungarian joint innovation cooperation and introduction of Hungarian advanced agricultural and environmental protection technologies.&amp;nbsp;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/c377e58528b9837c6ad0b50311eda454.jpg&quot; alt=&quot;&quot; /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;span style=&quot;line-height:1.5;&quot;&gt;Ms. Ágnes Kárteszi, Mr. Sándor Pákozdi (Middle) and Mr. Gyozo Jordan (second from right)&lt;/span&gt;\r\n&lt;/div&gt;');
INSERT INTO `ittc_content_news` VALUES('27','12','&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;2017年3月30-31日，欧盟中国联合创新中心黄淳副主任一行与来自匈牙利拥有土壤修复、生物质、微生物修复处理、植物修复等先进技术企业和研究机构，共同参与由匈牙利驻华大使馆举办的“中国—匈牙利农业&amp;amp;环境技术项目交流会”，期间与圣伊什万特大学环境科学院博士Gyozo Jordan先生、芦竹纤维农业有限公司执行经理Sándor Pákozdi 先生、匈牙利大驻华使馆第一秘书Ágnes Kárteszi女士就中匈联合创新合作，共同推进匈牙利高新农业与环保技术落户中国等事宜进行充分沟通，并达成合作共识。&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/c37855dd04f34c9f9e0329b51bba65c3.jpg&quot; alt=&quot;&quot; /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;br /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/c20a45194d6d47547fd417243b5556ae.jpg&quot; alt=&quot;&quot; /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;span style=&quot;line-height:1.5;&quot;&gt;Ágnes Kárteszi女士与Sándor Pákozdi先生（中）、Gyozo Jordan先生（右2）&lt;/span&gt;\r\n&lt;/div&gt;');
INSERT INTO `ittc_content_news` VALUES('28','12','&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;2017年3月26日应广东南沙自贸区邀请，欧盟中国联合创新中心一行对南沙新区进行考察，并与南沙自贸区相关领导就南沙自贸区同欧盟国家发展创新领域的交流及合作进行了深入研讨。&amp;nbsp;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/569142898c9ef4978944a4f1aeedd98b.jpg&quot; alt=&quot;&quot; /&gt; \r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;br /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/e531a531f3370ce479297ca6bb295f19.jpg&quot; alt=&quot;&quot; /&gt; \r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;br /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/83f50a4b25ea43b3118160b7dd30dd02.jpg&quot; alt=&quot;&quot; /&gt; \r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;span style=&quot;line-height:1.5;&quot;&gt;考察并听取自贸区规划介绍&lt;/span&gt; \r\n&lt;/div&gt;');
INSERT INTO `ittc_content_news` VALUES('29','35','&amp;nbsp; &amp;nbsp; &amp;nbsp;On March 26th，2017, the delegation headed by EUCJIC visited China (Guangdong) Pilot Free Trade Zone—Nansha Area of Guangzhou upon &amp;nbsp;invitation. The delegation met with relevant officials of the District and had detailed discussion of future cooperation between Nansha District and EU member states in the field of development and innovation.&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/85103313728fbca1bdba3bef4a792f4e.jpg&quot; alt=&quot;&quot; /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;span style=&quot;line-height:1.5;&quot;&gt;The delegation visited Nansha District&amp;nbsp;&lt;/span&gt;\r\n&lt;/div&gt;');
INSERT INTO `ittc_content_news` VALUES('30','35','&amp;nbsp; &amp;nbsp; &amp;nbsp;On March 25th，2017, the delegation headed by EUCJIC visited Huangpi district in Wuhan City upon &amp;nbsp;invitation of Huangpi district government. The delegation met with relavent officials of Huangpi district and reached several consensuses on EU-China cooperation.&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/bf44790a8c4c0ed6baa1739ef54eaee3.jpg&quot; alt=&quot;&quot; /&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;img src=&quot;/data/upload/image/201704/27921157c4301153721f33a3d5ea2dbf.jpg&quot; alt=&quot;&quot; /&gt;&lt;br /&gt;\r\n&lt;/div&gt;');
INSERT INTO `ittc_content_news` VALUES('31','12','&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;2017年3月25日 应湖北武汉市黄陂区政府邀请，欧盟中国联合创新中心一行对武汉市黄陂区进行考察访问，并在实地考察后与黄陂区政府就欧中合作事宜达成多项共识。&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/bbdbd2c2aca6a61c52b8b7f9ea4b6148.jpg&quot; alt=&quot;&quot; /&gt;\r\n&lt;/div&gt;\r\n&lt;br /&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/39c76f116be31175f35006b28773faba.jpg&quot; alt=&quot;&quot; /&gt;\r\n&lt;/div&gt;');
INSERT INTO `ittc_content_news` VALUES('32','12','&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;2017年3月27日 应广东肇庆国家高新技术产业开发区邀请， 欧盟中国联合创新中心一行对开发区进行了考察，并与广东肇庆国家高新技术产业开发区相关领导就肇庆市同欧盟国家发展创新领域的交流及合作进行了深入研讨。&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/9747078f1986d43a37cd8409d2078a5c.jpg&quot; alt=&quot;&quot; /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;br /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/2ddd1d06551f06bdeff8f4534aad4804.jpg&quot; alt=&quot;&quot; /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;br /&gt;\r\n&lt;/div&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/01a3b2a5eb09d4dd1b3c0a87db3e74e1.jpg&quot; alt=&quot;&quot; /&gt;\r\n&lt;/div&gt;');
INSERT INTO `ittc_content_news` VALUES('33','35','&amp;nbsp; &amp;nbsp; &amp;nbsp;On March 27th，2017, the delegation headed by &amp;nbsp;EUCJIC visited Zhaoqing High-Tech Industry Development Zone (ZHITDZ) upon invitation. The delegation met with relevant officials of ZHITDZ to discuss the issue of future cooperation between ZHITDZ and EU member states in the field of development and innovation.&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;div style=&quot;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/data/upload/image/201704/306cda74f2183bd9ed55283ff657f191.jpg&quot; alt=&quot;&quot; /&gt;\r\n&lt;/div&gt;');

DROP TABLE IF EXISTS `ittc_content_product`;
CREATE TABLE `ittc_content_product` (
  `id` mediumint(8) NOT NULL,
  `catid` smallint(5) NOT NULL,
  `content` mediumtext NOT NULL,
  PRIMARY KEY (`id`),
  KEY `catid` (`catid`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ittc_form_comment`;
CREATE TABLE `ittc_form_comment` (
  `id` mediumint(8) NOT NULL AUTO_INCREMENT,
  `cid` mediumint(8) NOT NULL,
  `userid` mediumint(8) NOT NULL,
  `username` char(20) NOT NULL,
  `listorder` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `status` tinyint(2) unsigned NOT NULL DEFAULT '1',
  `time` int(10) unsigned NOT NULL DEFAULT '0',
  `ip` char(20) DEFAULT NULL,
  `pinglunneirong` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `listorder` (`listorder`),
  KEY `status` (`status`),
  KEY `time` (`time`),
  KEY `userid` (`userid`),
  KEY `cid` (`cid`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ittc_form_gestbook`;
CREATE TABLE `ittc_form_gestbook` (
  `id` mediumint(8) NOT NULL AUTO_INCREMENT,
  `cid` mediumint(8) NOT NULL,
  `userid` mediumint(8) NOT NULL,
  `username` char(20) NOT NULL,
  `listorder` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `status` tinyint(2) unsigned NOT NULL DEFAULT '1',
  `time` int(10) unsigned NOT NULL DEFAULT '0',
  `ip` char(20) DEFAULT NULL,
  `nindexingming` varchar(255) DEFAULT NULL,
  `lianxiQQ` varchar(255) DEFAULT NULL,
  `liuyanneirong` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `listorder` (`listorder`),
  KEY `status` (`status`),
  KEY `time` (`time`),
  KEY `userid` (`userid`),
  KEY `cid` (`cid`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ittc_member`;
CREATE TABLE `ittc_member` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `username` char(20) NOT NULL DEFAULT '',
  `password` char(32) NOT NULL DEFAULT '',
  `email` varchar(100) NOT NULL,
  `avatar` varchar(100) NOT NULL DEFAULT '',
  `modelid` smallint(5) NOT NULL,
  `regdate` int(10) unsigned NOT NULL DEFAULT '0',
  `regip` varchar(50) NOT NULL,
  `status` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  UNIQUE KEY `username` (`username`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ittc_member_connect`;
CREATE TABLE `ittc_member_connect` (
  `uid` mediumint(9) NOT NULL,
  `openid` varchar(32) NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ittc_member_geren`;
CREATE TABLE `ittc_member_geren` (
  `id` mediumint(8) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ittc_model`;
CREATE TABLE `ittc_model` (
  `modelid` smallint(5) unsigned NOT NULL AUTO_INCREMENT,
  `typeid` tinyint(3) NOT NULL,
  `modelname` char(30) NOT NULL,
  `tablename` char(20) NOT NULL,
  `listtpl` varchar(30) NOT NULL,
  `showtpl` varchar(30) NOT NULL,
  `joinid` smallint(5) DEFAULT NULL,
  `setting` text,
  PRIMARY KEY (`modelid`),
  KEY `typeid` (`typeid`),
  KEY `joinid` (`joinid`)
) ENGINE=MyISAM AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

