DROP TABLE IF EXISTS `ittc_site`;
CREATE TABLE `ittc_site` (
  `siteid` smallint(5) NOT NULL AUTO_INCREMENT,
  `site_name` varchar(50) NOT NULL,
  `site_title` varchar(255) NOT NULL,
  `site_theme` varchar(100) NOT NULL,
  `site_keywords` varchar(100) NOT NULL,
  `site_description` varchar(255) NOT NULL,
  `site_domain` varchar(200) NOT NULL,
  `site_language` varchar(20) NOT NULL,
  `language` varchar(50) NOT NULL,
  PRIMARY KEY (`siteid`)
) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

